<?phpclass fpdf_relatorio{	public $bol_fonte_utf8;	public $str_campo_descricao;	public $int_imagem_altura;	public $int_imagem_largura;	public $obj_entidade;	public function __construct(){		$this->bol_fonte_utf8 = true;		$this->int_imagem_largura = 53;		$this->int_imagem_altura = 18;		$this->str_estado = "Santa Catarina";		$this->str_cidade = "Blumenau";		$this->str_bairro = "Nova esperena";		$this->str_rua = "Salete, 164 - AP 04";		$this->str_CEP = "8905-1650";		$this->str_url_pagina = "www.developwd.com";		$this->str_email = "robsonromeu@yahoo.com.br";		$this->str_telefone = "(47)8452-5094";	}  private function tipo_pagina(){    if ($this->obj_entidade->rel_tipo_pagina == "H"){      $tipo_pagina = "L";    }else{    	$tipo_pagina = "P";    }    return $tipo_pagina;  }  public function criar(){		$obj_pdf = new fpdf_dwd($this->tipo_pagina());		$obj_pdf->obj_entidade = $this->obj_entidade;		if ($str_tamanho_margem_esquerda != ""){		  $obj_pdf->str_campo_margem_esquerda = $str_tamanho_margem_esquerda;		}		//Imagem cabecalho		$obj_pdf->int_imagem_largura = $this->int_imagem_largura;		$obj_pdf->int_imagem_altura = $this->int_imagem_altura;		$obj_pdf->str_estado = utf8_decode($this->str_estado);		$obj_pdf->str_cidade = utf8_decode($this->str_cidade);		$obj_pdf->str_bairro = utf8_decode($this->str_bairro);		$obj_pdf->str_rua = utf8_decode($this->str_rua);		$obj_pdf->str_CEP = utf8_decode($this->str_CEP);		$obj_pdf->str_url_pagina = utf8_decode($this->str_url_pagina);		$obj_pdf->str_email = utf8_decode($this->str_email);		$obj_pdf->str_telefone = utf8_decode($this->str_telefone);		$obj_pdf->criar();  }}?>