<?php$limitar_ext = "sim";$extensoes_validas = explode(";wd;",$_GET["str_tipo_arquivo"]);$str_campo_arquivo = "arq_upl_".$_GET["str_nome"];$str_campo_local = "arq_loc_".$_GET["str_nome"];$caminho_absoluto = $_POST[$str_campo_local];$limitar_tamanho = "nao";$tamanho_bytes = "200000";$sobrescrever = "nao";/*set_time_limit(0);*/$nome_arquivo = $_FILES[$str_campo_arquivo]['name'];$tamanho_arquivo = $_FILES[$str_campo_arquivo]['size'];$arquivo_temporario = $_FILES[$str_campo_arquivo]['tmp_name'];if (!empty ($nome_arquivo)){	if ($sobrescrever == "nao" && file_exists($caminho_absoluto."/".$nome_arquivo))		die("Arquivo já existe.");	if (($limitar_tamanho == "sim") && ($tamanho_arquivo > $tamanho_bytes))		die("Arquivo deve ter no máximo $tamanho_bytes bytes.");	$ext = strrchr($nome_arquivo,'.');	$ext = strtolower($ext);	if ($limitar_ext == "sim" && !in_array($ext,$extensoes_validas))		die("Extensão de arquivo inválida para upload.");	if(move_uploaded_file($arquivo_temporario, $caminho_absoluto."/".$nome_arquivo))	{		echo "<p align=center>O upload do arquivo <b>". $nome_arquivo."</b> foi concluído com sucesso.</p>";	}else		echo "<p align=center>O arquivo não pode ser copiado para o servidor.</p>";}else if (isset($_GET["str_nome"]))	die("Selecione o arquivo a ser enviado");?>